import React, { useState } from "react";
import { Navbar, Container, Form, Button, Col } from "react-bootstrap";
import logo from "../Assets/imagixlogo.jpg";

const Login = () => {
  // States and const variable

  const [values, setValues] = useState({
    username: "",
    password: "",
    details: "",
  });

  // function for loggin in

  const login = (e) => {
    e.preventDefault();
    setValues({ ...values, details: "clicked" });

    console.log({ details });
  };

  return (
    <div className="container ">
      <Navbar bg="dark">
        <Container>
          <Navbar.Brand className="text-light">
            <img
              src={logo}
              width="50"
              height="50"
              className="d-inline-block align-top"
              alt="Imagix Media Logo"
            />
            Imagix Media
          </Navbar.Brand>
        </Container>
      </Navbar>
      <Col xs={12} className="d-flex justify-content-center">
        <Form className="d-flex flex-column border border-primary rounded-4 justify-content-center mt-5 p-5">
          <h1>Login</h1>

          {/* Input for username */}
          <Form.Group className="mb-4">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="email"
              placeholder="Username"
              name="username"
              onChange={(e) => {
                setValues({ ...values, [e.target.name]: e.target.value });
              }}
            />
          </Form.Group>

          {/* Input for password */}
          <Form.Group className="mb-4">
            <Form.Label>Enter a Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Enter Password"
              name="password"
              onChange={(e) => {
                setValues({ ...values, [e.target.name]: e.target.value });
              }}
            />
          </Form.Group>
          <p></p>
          <Button className="mb-3" variant="primary" onClick={login}>
            Login
          </Button>
          <a href="">Forgot Password</a>
        </Form>
      </Col>
    </div>
  );
};

export default Login;
